var textSpeed = 150;					//speed of text animation
var textTransIn = "linear";	//transition IN for text animation
var textTransOut = "linear";	//transition OUT for text animation
var textColor = "#a49050";			//color of text in normal state
var textHoverColor = "#ffffff";		//color of text in highlighted state
var subTextColor = "#a49050";		//color of sub menu text in normal state
var subBGColor = "#ffffff";			//color of sub menu option background
var subTextHoverColor = "#a49050";	//color of sub menu text in highlighted state
var subBGHoverColor = "#ffffff";	//color of sub menu option background in highlighted state
var sliderSpeed = 800;				//speed of slider animation
var sliderTrans = "easeOutExpo";	//transition for slider animation
var subSpeedOn = 200; 				//speed the sub menus appear
var subSpeedOff = subSpeedOn; 		//speed the sub menus disappear
var subDelay1 = 0; 				//delay before sub menu 1 appears
var subDelay2 = 0;					//delay before sub menu 2 appears
var parentLink;
var subParentLink;

	
(function($) {
	$.fn.lavaLampV = function(o) {
		o = $.extend({ fx: sliderTrans, speed: sliderSpeed, click: function(){} }, o || {});
		return this.each(function() {
			
			var whichPage = 0;				
			var offPage = false;

			if (window.location.pathname == "/index.php" || window.location.pathname == "/") {
				whichPage = 0;
				offPage = false;
			}	
			if (window.location.pathname == "/about-us.php") {
				whichPage = 1;
				offPage = false;
			}	
			if (window.location.pathname == "/leadership.php") {
				whichPage = 2;
				offPage = false;
			}	
			if (window.location.pathname == "/bylaws.php") {
				whichPage = 3;
				offPage = false;
			}	
			if (window.location.pathname == "/events.php") {
				whichPage = 4;
				offPage = false;
			}	
			if (window.location.pathname == "/officer-of-the-month.php") {
				whichPage = 5;
				offPage = false;
			}	
			if (window.location.pathname == "/contact-us.php") {
				whichPage = 6;
				offPage = false;
			}	
			
			var me = $(this), noop = function(){}, $back = $('<li class="back"><div class="left"></div></li>').appendTo(me), $li = $(">li", this), curr = $("li.current", this)[0] || $($li[whichPage]).addClass("current")[0];
				
			$li.not(".back").hover(function() { move(this); }, noop);
	
			$(this).hover(noop, function() { move(curr); });
	
			$li.click(function(e) {
				setCurr(this);
				return o.click.apply(this, [e, this]);
			});
	
			setCurr(curr);

			function setCurr(el) {
				if (offPage == false) { $("li.current").find("span").css('color',textHoverColor); }
				if (offPage == true) { newTop = 99999; } else { newTop =  el.offsetTop; }
				$back.css({ "top": newTop+"px", "height": el.offsetHeight+"px" });
				curr = el;
			};

			function move(el) { 
			 	$back.each(function() { $.dequeue(this, "fx"); }).delay(25).animate({ height: el.offsetHeight, top: el.offsetTop }, o.speed, o.fx); 
			};
	
		});
	};
})(jQuery);

jQuery(function(){
    
    var $ = jQuery;
    // retarder
    $.fn.retarder = function(delay, method){
        var node = this;
        if (node.length){
            if (node[0]._timer_) clearTimeout(node[0]._timer_);
            node[0]._timer_ = setTimeout(function(){ method(node); }, delay);
        }
        return this;
    };
	
	// control how the sub menu 1 appears & disappears
	$('.menu>li').hover (
        function(){
            var ul = $('div:first', this);
            if (ul.length){
                if (!ul[0].wid) ul[0].wid = ul.width();
                ul.css({width: 0, overflow: 'hidden'}).retarder(subDelay1, function(i){
                    i.css('visibility', 'visible').animate({width: ul[0].wid}, {duration: subSpeedOn, complete : function(){ ul.css('overflow', 'visible'); }});
					colorCorrect = setInterval(function(){
						parentLink.find("span").stop(true, true).css('color',textHoverColor);									
					}, 1);
                });
            }
        },
        function(){
            var ul  = $('div:first', this);
            if (ul.length){
                var css = {visibility: 'hidden', width: ul[0].wid};
                ul.stop(true,true).retarder(1, function(i) { 
					i.animate({width: 0}, {duration: subSpeedOff, complete : function(){ i.css('visibility', 'hidden'); }});
				});
				clearInterval(colorCorrect);
				setTimeout(function(){
					$("li").find("span").css('color',textColor);			
				}, 25);
            }
        }
    );    
	// control how the sub menu 2 appears & disappears
	$('ul ul li').hover(
		function() {
			var ul = $('div:first', this);
			if (ul.length){
				if (!ul[0].wid) ul[0].wid = ul.width();
				ul.css({width: 0, overflow: 'hidden'}).retarder(subDelay1, function(i){
					i.css('visibility', 'visible').animate({width: ul[0].wid}, {duration: subSpeedOn, complete : function(){ ul.css('overflow', 'visible'); }});
					subColorCorrect = setInterval(function(){
						subParentLink.find("span").stop(true, true).css({ 'color':subTextHoverColor, 'background-color':subBGHoverColor });									
					}, 1);
				});
				
			}
		},
		function(){
			var ul  = $('div:first', this);
			if (ul.length){
				ul.stop().retarder(1, function(i) { 
					i.animate({width: 0}, {duration: subSpeedOff, complete : function(){ i.css('visibility', 'hidden'); }});
				});
				clearInterval(subColorCorrect);
				setTimeout(function(){
					subParentLink.find("span").css({ 'color':subTextColor, 'background-color':subBGColor });									
				}, 25);
			}
		}
	);

	//control slider and text animation	
    setTimeout(function(){ $('#menu ul.menu').lavaLampV({ speed: sliderSpeed }); }, 100);  

	$('ul a.parent').mouseover(  				
		function(){ 
			parentLink = $(this);
		}
	); 
	$('ul a.subParent').mouseover(  				
		function(){ 
			subParentLink = $(this);
		}
	); 

	$('ul a span').hover(  				
		function(){ 
			$("li.current").find("span").stop(true, true).delay(25).animate({color: textColor}, textSpeed, textTransIn); 
			$(this).stop(true, true).delay(25).animate({color: textHoverColor}, textSpeed, textTransOut);
		},
		function(){ 
			$(this).stop(true, true).delay(25).animate({color: textColor}, textSpeed, textTransIn); 
			$("li.current").find("span").stop(true, true).delay(25).animate({color: textHoverColor}, textSpeed, textTransOut);
		}
	); 
});